mapper.update:
UPDATE HH_USER
SET
USER_POINT = #{userPoint}
WHERE ID = #{userId}

 UPDATE DEAL_HISTORY
		SET
		P_CHECK = SYSDATE WHERE SELL_SEQ = #{proSeq}

mapper.delete:
DELETE FROM JJIM
WHERE PRODUCT_SEQ IN

	#{proSeq}

AND ID = #{userId}

mapper.select:
SELECT HH_USER.*, ROUND(SYSDATE - CREATE_DATE) AS CREATE_DIFF
FROM HH_USER
WHERE ID = #{id}

SELECT COUNT(*)
FROM HH_USER
WHERE NICK_NAME = #{nickName}

mapper.update:
UPDATE HH_USER
SET
PHONE_NUM = #{phone}
WHERE ID = #{userId}

UPDATE HH_USER
SET
ADDRESS = #{address},
DETAIL_ADDRESS = #{detailAddress}
WHERE ID =
#{userId}

UPDATE CHAT_ROOM
SET SELLER = NULL
WHERE SELLER = #{userId}

UPDATE CHAT_ROOM
SET BUYER = NULL
WHERE BUYER = #{userId}

mapper.select:
SELECT *
FROM IMG_FILES
WHERE ID = #{userId}
AND IMG_CATE_SEQ = 4
AND TABLE_PK =
#{seqQnaBno}

mapper.delete:
DELETE FROM IMG_FILES
WHERE img_cate_seq = 4
AND table_pk = #{seqQnaBno}

mapper.update:
UPDATE HH_USER
SET
PW = #{pw}
WHERE ID = #{userId}
