mapper.select:
SELECT ID username, NAME username, PW password, ROLE role
FROM HH_USER
WHERE ID = #{username}

SELECT ID user_id, NAME username, PW password, ROLE role
FROM HH_USER
WHERE ID = #{userId}

SELECT ID user_id, NAME username, PW password, ROLE role
FROM HH_USER
WHERE EMAIL = #{email}

SELECT NVL(NICK_NAME, '손님') AS nickName
FROM HH_USER
WHERE ID = #{userId}
